sys_script_include.script:
var UIPage_Generator = Class.create();
UIPage_Generator.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    type: 'UIPage_Generator',

	generate: function(fieldname, table, query) {
		fieldname = fieldname || this.getParameter('sysparm_fieldname');
		table = table || this.getParameter('sysparm_table');
		query = query || this.getParameter('sysparm_query');

		var response = {
			success: false,
			message: "Something went wrong.",
			data: false
		};
		if (!fieldname || !table || !query) {
			response.message = "Invalid parameter value. Please try again.";
		} else {
			var result = [];
			var gr = new GlideRecord(table);
			gr.addEncodedQuery(query);
			gr.query();
			while (gr.next()) {
				var localresponse = new QRMan().createQRFromFieldValue(fieldname, table, gr.getUniqueValue());
				result.push(JSON.parse(localresponse));
			}

			response.success = true;
			response.message = "Success (please ensure you have the correct value in \"data\" attribute).";
			response.data = result;
		}

		return JSON.stringify(response, null, 2);
	}

});
